const LOCAL_STORAGE_KEY = 'OAuthSessionModel';

/**
 * A model representing a Player.me OAuth session.
 * @memberOf module:api/auth
 */
class OAuthSessionModel {
    /**
     * Create a new Activity model.
     * @param {Object} [obj] A player response object to initialise this model with.
     */
    constructor(obj=null)
    {
        this._accessToken  = obj && obj.access_token  || '';
        this._refreshToken = obj && obj.refresh_token || '';
        this._tokenType    = obj && obj.token_type    || '';
        this._expires      = obj && obj.expires       || 0;
        this._expiresIn    = obj && obj.expires_in    || 0;

        this._expires = this._expires ? new Date(this._expires*1000) : null;

        this._raw = obj;
    }

    toString() {
        var msg = '[OAuthSession';
        if (this._tokenType){
            msg += ' '+this._tokenType;
        }
        return msg +']';
    }

    /**
     * The OAuth session's active token.
     * @readonly
     * @member {string} OAuthSessionModel#accessToken
     * @returns {string}
     */
    get accessToken(){
        return this._accessToken;
    }

    /**
     * The OAuth session's refresh token.
     * @readonly
     * @member {string} OAuthSessionModel#refreshToken
     * @returns {string}
     */
    get refreshToken(){
        return this._refreshToken;
    }

    /**
     * The OAuth session's token type.
     * i.e. "bearer"
     * @readonly
     * @member {string} OAuthSessionModel#tokenType
     * @returns {string}
     */
    get tokenType(){
        return this._tokenType;
    }

    /**
     * The date this session lasts until
     * @readonly
     * @member {Date} OAuthSessionModel#expires
     * @returns {Date}
     */
    get expires(){
        return this._expires;
    }

    /**
     * The number of seconds until this session expires, since the session was last updated.
     * @readonly
     * @member {int} OAuthSessionModel#expiresIn
     * @returns {int}
     */
    get expiresIn(){
        return this._expiresIn;
    }

    /**
     * Returns the string we're expected to pass to Player in a header.
     * @returns {string}
     *
     * @see http://docs.playerme.apiary.io/#reference/general/example-authenticated-requests/with-request-headers-oauth-only
     */
    toHeaderString(){
        if (this.tokenType && this.accessToken) {
            // Capitalise first letter of tokenType
            var tokenType = this.tokenType.charAt(0).toUpperCase() + this.tokenType.slice(1);
            return tokenType + " " + this.accessToken;
        }
        return "";
    }

    /**
     * Add this session to storage
     * TODO Cookie fallback
     */
    addToLocalStorage(){
        if (typeof localStorage == 'undefined') return;
        localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(this._raw));
    }

    /**
     * Get a model from local storage
     * @returns {OAuthSessionModel}
     */
    static getFromLocalStorage(){
        if (typeof localStorage == 'undefined') return null;
        var json = localStorage.getItem(LOCAL_STORAGE_KEY);
        var parsed = JSON.parse(json);

        return parsed ? new OAuthSessionModel(parsed) : null;
    }

    /**
     * Remove a modal from local storage
     */
    static removeFromLocalStorage(){
        if (typeof localStorage == 'undefined') return;
        localStorage.removeItem(LOCAL_STORAGE_KEY);
    }
}

export default OAuthSessionModel;

import AbstractResponse from '../request/response/AbstractResponse';
import OAuthSessionModel from './OAuthSessionModel';

/*
201
{
  "access_token":  "$ACCESS_TOKEN",
  "token_type":    "bearer",
  "expires":       $EXPIRATION_TIME,
  "expires_in":    $EXPIRATION_TIME_FROM_NOW,
  "refresh_token": "$REFRESH_TOKEN"
}

400
{
  "error":             "invalid_request",
  "error_description": "The user credentials were incorrect."
}

401
{
  "error":             "access_denied",
  "error_description": "You have enabled 2-Factor authentication. Please click your link in the email."
}
 */

/**
 * Response containing OAuth tokens
 * @extends AbstractResponse
 * @memberOf module:api/auth
 */
class OAuthSessionResponse extends AbstractResponse {
    constructor(rawResponse)
    {
        super(rawResponse);

        var session = new OAuthSessionModel(rawResponse.body);
        this._result = null;

        this._success = this.statusCode >= 200 && this.statusCode < 300 && session.accessToken;
        if (this._success){
            this._result = session;
        }
    }

    /**
     * The response body
     * @returns {OAuthSessionModel}
     */
    get result(){
        return this._result;
    }

    /**
     * Whether this response was successful
     * @returns {boolean}
     * @readonly
     */
    get success(){
        return this._success;
    }
}

export default OAuthSessionResponse;

import AbstractRequestAdapter from './adapter/AbstractRequestAdapter';

/**
 * A singleton wrapper for Request adapters
 * @extends AbstractRequestAdapter
 * @memberOf module:api/request
 */
class APIService extends AbstractRequestAdapter {

    //
    // Constructor
    //

    constructor()
    {
        super();

        /**
         * The URL that gets prepended to partial request URLs
         * @member {string}
         * @private
         */
        this._baseUrl = '';

        /**
         * The adapter used to make requests
         * @member {object}
         * @private
         */
        this._adapter = null;

        // Set defaults
        this.baseUrl = "https://player.me";
    }

    //
    // Request Methods
    //

    // TODO Have success & failure classes
    _request(method, url, data){
        var requestBodyRequired = false;
        switch(method){
            case 'get':  requestBodyRequired = false; break;
            case 'post': requestBodyRequired = true;  break;
            case 'put':  requestBodyRequired = true;  break;
            case 'del':  requestBodyRequired = false; break;
            default: throw new Error("Unhandled method ["+method+"] passed to APIService._request().");
        }

        validateURL(method, url);
        validateData(method, data, requestBodyRequired);

        var adapter = this.getAdapter(true);
        url = this.prependBaseUrl(url);
        return adapter[method](url, data);
    }

    get(url, data){
        return this._request('get', url, data);
    }

    post(url, data){
        return this._request('post', url, data);
    }

    put(url, data){
        return this._request('put', url, data);
    }

    del(url, data){
        return this._request('del', url, data);
    }

    //
    // Base URL methods
    //

    /**
     * @param {string} str
     */
    set baseUrl(str){
        if (typeof str !== 'string'){
            throw new TypeError("baseUrl passed to APIService is not a string");
        }
        if (str.length == 0){
            throw new TypeError("baseUrl passed to APIService is empty");
        }
        // Must have '://' and at least one character before it
        if (str.indexOf('://') <= 0){
            throw new TypeError("baseUrl is missing protocol");
        }
        this._baseUrl = str;
    }

    /**
     * @returns {string}
     */
    get baseUrl(){
        return this._baseUrl;
    }

    /**
     * Add the baseUrl to the start of the passed address
     * @param {string} url
     * @returns {string}
     */
    prependBaseUrl(url){
        // Skip if passed url has a protocol
        if (url.indexOf('://') == -1){
            var baseUrl = this._baseUrl;

            // Remove slash at end
            if (baseUrl.charAt(baseUrl.length-1) === '/'){
                baseUrl = baseUrl.substring(0, baseUrl.length-1);
            }
            // Remove slash at start
            if (url.charAt(0) === '/'){
                url = url.substring(1);
            }

            // Add base to url
            url = baseUrl + '/' + url;
        }
        return url;
    }

    //
    // Adapter Methods
    //

    /**
     * Get the current adapter
     * @param {boolean} [validate=false] Validate the adapter before returning it
     * @returns {object} The adapter
     * @throws {TypeError} If adapter is invalid
     * @throws {ReferenceError} If no adapter is set
     */
    getAdapter(validate=false){
        if (!this._adapter) {
            throw new ReferenceError("APIService error: No adapter set");
        }
        if (validate){
            this.validateAdapter(this._adapter);
        }
        return this._adapter;
    }

    /**
     * Set the adapter
     * @param {AbstractRequestAdapter} adapter Adapter instance or class
     * @throws {TypeError} If adapter is invalid
     */
    setAdapter(adapter){
        // Turn class into an instance
        if (typeof adapter === 'function' && adapter.constructor){
            adapter = new adapter();
        }

        this.validateAdapter(adapter);
        this._adapter = adapter;
    }

    /**
     * Throws an error if the adapter is invalid
     * @param {object} adapter The adapter to validate
     * @throws {TypeError}
     */
    validateAdapter(adapter){
        // Assert adapter is an object
        if (typeof adapter !== 'object') {
            throw new TypeError('Invalid APIRequest adapter: Not an object.');
        }

        // Assert adapter inherits from AbstractRequestAdapter
        var adapterClass = adapter.constructor;
        while (adapterClass){
            if (adapterClass === AbstractRequestAdapter) return;
            adapterClass = Object.getPrototypeOf(adapterClass);
        }
        throw new TypeError("Invalid APIRequest adapter: Doesn't inherit from AbstractRequestAdapter.");
    }

    /// End of APIService
}

//
// Inaccessible Validation Methods
//

/**
 * Validate that the URL is valid
 * @param {string} method Name of the method this is being called in
 * @param {string} url The request URL
 * @throws {ReferenceError}
 * @ignore
 */
function validateURL(method, url){
    var msg;
    if (!url){
        msg = "No URL passed to APIService:"+method+".";
        console.error(msg);
        throw new ReferenceError(msg);
    }
    if (typeof url != 'string'){
        msg = "Invalid URL passed to APIService:"+method+". Was ["+typeof url+"].";
        console.error(msg);
        throw new ReferenceError(msg);
    }
}

/**
 * Validate that the data object is valid
 * @param {string} method Name of the method this is being called in
 * @param {data} data The data to be used in the request
 * @param {boolean} required If the request body should be truthy
 * @throws {ReferenceError}
 * @ignore
 */
function validateData(method, data, required){
    var msg;
    if (!required && !data){
        return;
    }
    if (typeof data != 'object'){
        msg = "Invalid data passed to APIService:"+method+". Was ["+typeof data+"].";
        console.error(msg);
        throw new ReferenceError(msg);
    }
}

// Export a singleton instance
export default new APIService;

/**
 *
 * @memberOf module:api/request/adapter
 */
class AbstractRequestAdapter {
    constructor()
    {
        var abstractClassName = 'AbstractRequestAdapter';
        if (this.name === abstractClassName){
            throw new Error(abstractClassName+' was not supposed to be instantiated.');
        }
    }

    /**
     * The adapter's name
     * @returns {string}
     */
    get name(){
        return this.constructor.name;
    }

    /**
     * Submit request
     * @param {string} method
     * @param {string} url
     * @param {object} data
     * @return Promise
     * @throws Error Error when not implemented
     * TODO Create not-implemented error
     */
    request(method, url, data){
        throw new Error("request() not implemented by "+this.name);
    }

    /**
     * Submit a GET request
     * @param {string} url
     * @param {object} data
     * @return Promise
     * @throws Error Error when not implemented
     * TODO Create not-implemented error
     */
    get(url, data){
        throw new Error("GET not implemented by "+this.name);
    }

    /**
     * Submit a POST request
     * @param {string} url
     * @param {object} data
     * @return Promise
     * @throws Error Error when not implemented
     * TODO Create not-implemented error
     */
    post(url, data){
        throw new Error("POST not implemented by "+this.name);
    }

    /**
     * Submit a PUT request
     * @param {string} url
     * @param {object} data
     * @return Promise
     * @throws Error Error when not implemented
     * TODO Create not-implemented error
     */
    put(url, data){
        throw new Error("PUT not implemented by "+this.name);
    }

    /**
     * Submit a DELETE request
     * @param {string} url
     * @param {object} data
     * @return Promise
     * @throws Error Error when not implemented
     * TODO Create not-implemented error
     */
    del(url, data){
        throw new Error("DEL not implemented by "+this.name);
    }

    /**
     * Turn an object into a query string
     * @param {string} url
     * @param {object} obj
     * @param {boolean} [keepExisting=true] When true, any keys in `obj` that are in the `url`'s query string will be ignored
     * @returns {string}
     */
    addToQueryString(url, obj, keepExisting=true){
        // Validation
        if (!url){
            throw new ReferenceError('URL not passed to addToQueryString()');
        }
        if (typeof url !== 'string'){
            throw new TypeError('URL passed to addToQueryString() is not a string ('+typeof url+').');
        }
        if (typeof obj !== 'object'){
            throw new TypeError('obj passed to addToQueryString() is not an object ('+typeof url+').');
        }

        var resultParts = [];
        if (obj !== null) {

            // Remove URL's query string and add to obj
            var urlParts = url.split('?', 2);
            if (urlParts.length > 1) {
                url = urlParts[0]; // Remove query string from URL

                // For each part of the query string...
                var queryStringArr = urlParts[1].split('&');
                var queryStringLength = queryStringArr.length;

                for (var i = 0; i < queryStringLength; i++) {
                    var queryStringItem = queryStringArr[i].split('=', 2);
                    var queryStringKey = queryStringItem[0];

                    // If this should override obj, or it doesn't exist on obj, then add it
                    if (keepExisting || !obj.hasOwnProperty(queryStringKey)) {
                        obj[queryStringKey] = queryStringItem[1];
                    }
                }
            }

            // Convert obj into uri component array
            for (var objKey in obj) {
                if (obj.hasOwnProperty(objKey)) {
                    resultParts.push(objKey + '=' + encodeURIComponent(obj[objKey]));
                }
            }
        }

        // Return
        var result = url;
        if (resultParts.length){
            result += '?'+resultParts.join('&').replace(/%20/g, '+');
        }
        return result;
    }
}

