    // </editor-fold> Listeners
    // <editor-fold desc="Listener Methods">

    /**
     * Start listening for server-sent events from Sails with the specified eventIdentity.
     * Will trigger the provided callback function when a matching event is received.
     * @see http://sailsjs.org/documentation/reference/web-sockets/socket-client/io-socket-on
     *
     * @param {string} eventIdentity The unique identity of a server-sent event, e.g. "recipe"
     * @param {function} callback    Will be called when the server emits a message to this socket.
     * @return {Sails} Itself
     *
     * @throws ReferenceError When a parameter wasn't specified
     * @throws TypeError When a parameter is the wrong type
     * @throws Error When the socket wasn't created first
     */
    on(eventIdentity, callback){
        this._validateEventListener(this.constructor.name+'.on()', eventIdentity, callback);
        this._socket.on(eventIdentity, callback);
        return this;
    }

    /**
     * Unbind the specified event handler (opposite of .on()).
     *
     * If you decide to use this method, be careful!
     * off() does not stop the this client-side socket from receiving any server-sent messages,
     * it just prevents the specified event handler from firing.
     * @see http://sailsjs.org/documentation/reference/web-sockets/socket-client/io-socket-off
     *
     * @param {string} eventIdentity The unique identity of a server-sent event, e.g. "recipe"
     * @param {function} callback    Will be called when the server emits a message to this socket.
     * @return {Sails} Itself
     *
     * @throws ReferenceError When a parameter wasn't specified
     * @throws TypeError When a parameter is the wrong type
     * @throws Error When the socket wasn't created first
     */
    off(eventIdentity, callback){
        this._validateEventListener(this.constructor.name+'.off()', eventIdentity, callback);
        this._socket.off(eventIdentity, callback);
        return this;
    }

    // </editor-fold> Listener Methods
    // <editor-fold desc="Request Methods">

    /**
     * Send a virtual request to a Sails server using Socket.io.
     * @param {string}   method     The HTTP request method. (e.g. get/post/put/delete)
     * @param {string}   url        The destination URL path. (e.g. "/checkout")
     * @param {object}   [data]     Data to be included as the request body.
     * @param {object}   [headers]  Dictionary of request headers.
     * @param {function} [callback] Takes a body object and an object with headers, body and statusCode.
     * @return {Sails} Itself
     *
     * @see http://sailsjs.org/documentation/reference/web-sockets/socket-client/io-socket-request
     * @throws ReferenceError When an argument is missing.
     * @throws TypeError      When an argument is the wrong type.
     * @throws RangeError     When an invalid method is passed.
     * @throws Error          When the socket doesn't exist.
     */
    request(method, url, data, headers, callback){
        // Validate
        var functionName = this.constructor.name+'.request()';
        this._validateRequest(functionName, url, data, callback);

        // Check method passed
        if (!method){
            throw new ReferenceError(
                "No `method` passed to "+functionName+"."
            );
        }

        // Check method is a string
        if (typeof method !== 'string'){
            throw new TypeError(
                "`method` passed to "+functionName+" was not a string. ["+typeof method+"]"
            );
        }

        // Check method is valid
        var availableMethods = ['get', 'post', 'put', 'delete'];
        method = method.toLowerCase();
        if (availableMethods.indexOf(method) < 0){
            throw new RangeError(
                "`method` passed to "+functionName+" is not available. ["+availableMethods.join('/')+"]"
            );
        }

        // Validate headers
        if (headers && typeof headers !== 'object'){
            throw new TypeError("`headers` passed to "+functionName+" was not an object. ["+typeof headers+"]");
        }

        // Execution
        var params = {
            method: method,
            url: url
        };
        if (data){
            params.data = data;
        }
        if (headers){
            params.headers = headers;
        }
        this._socket.request(params, callback);
        return this;
    }

    /**
     * Send a socket request (virtual GET) to a Sails server using Socket.io.
     * @param {string}   url      The destination URL path; e.g. "/checkout".
     * @param {object}   [data]   Data to be included as the request body.
     * @param {function} callback Takes a body object and an object with headers, body and statusCode.
     * @return {Sails} Itself
     *
     * @see http://sailsjs.org/documentation/reference/web-sockets/socket-client/io-socket-get
     * @throws ReferenceError When an argument is missing.
     * @throws TypeError      When an argument is the wrong type.
     * @throws Error          When the socket doesn't exist.
     */
    get(url, data, callback){
        this._validateRequest(this.constructor.name+'.get()', url, data, callback);
        this._socket.get(url, data, callback);
        return this;
    }

    /**
     * Send a socket request (virtual POST) to a Sails server using Socket.io.
     * @param {string}   url      The destination URL path; e.g. "/checkout".
     * @param {object}   [data]   Data to be included as the request body.
     * @param {function} callback Takes a body object and an object with headers, body and statusCode.
     * @return {Sails} Itself
     *
     * @see http://sailsjs.org/documentation/reference/web-sockets/socket-client/io-socket-post
     * @throws ReferenceError When an argument is missing.
     * @throws TypeError      When an argument is the wrong type.
     * @throws Error          When the socket doesn't exist.
     */
    post(url, data, callback){
        this._validateRequest(this.constructor.name+'.post()', url, data, callback);
        this._socket.post(url, data, callback);
        return this;
    }

    /**
     * Send a socket request (virtual PUT) to a Sails server using Socket.io.
     * @param {string}   url      The destination URL path; e.g. "/checkout".
     * @param {object}   [data]   Data to be included as the request body.
     * @param {function} callback Takes a body object and an object with headers, body and statusCode.
     * @return {Sails} Itself
     *
     * @see http://sailsjs.org/documentation/reference/web-sockets/socket-client/io-socket-put
     * @throws ReferenceError When an argument is missing.
     * @throws TypeError      When an argument is the wrong type.
     * @throws Error          When the socket doesn't exist.
     */
    put(url, data, callback){
        this._validateRequest(this.constructor.name+'.put()', url, data, callback);
        this._socket.put(url, data, callback);
        return this;
    }

    /**
     * Send a socket request (virtual DELETE) to a Sails server using Socket.io.
     * @param {string}   url      The destination URL path; e.g. "/checkout".
     * @param {object}   [data]   Data to be included as the request body.
     * @param {function} callback Takes a body object and an object with headers, body and statusCode.
     * @return {Sails} Itself
     *
     * @see http://sailsjs.org/documentation/reference/web-sockets/socket-client/io-socket-delete
     * @throws ReferenceError When an argument is missing.
     * @throws TypeError      When an argument is the wrong type.
     * @throws Error          When the socket doesn't exist.
     */
    del(url, data, callback){
        this._validateRequest(this.constructor.name+'.del()', url, data, callback);
        this._socket['delete'](url, data, callback);
        return this;
    }

    // </editor-fold> Request Methods
    // <editor-fold desc="Validation">

    /**
     * Check that arguments for on()/off() are valid.
     *
     * @param {string}   functionName  The method to be mentioned in error messages.
     * @param {string}   eventIdentity The unique identity of a server-sent event, e.g. "recipe".
     * @param {function} callback      Will be called when the server emits a message to this socket.
     *
     * @protected
     * @throws ReferenceError When a parameter wasn't specified
     * @throws TypeError When a parameter is the wrong type
     * @throws Error When the socket wasn't created first
     */
    _validateEventListener(functionName, eventIdentity, callback){
        if (!eventIdentity){
            throw new ReferenceError(
                "No eventIdentity passed to "+functionName+"."
            );
        }
        if (typeof eventIdentity !== 'string'){
            throw new TypeError(
                "eventIdentity passed to "+functionName+" was not a string. ["+typeof eventIdentity+"]"
            );
        }
        if (!callback){
            throw new ReferenceError(
                "No callback passed to "+functionName+"."
            );
        }
        if (typeof callback !== 'function'){
            throw new TypeError(
                "callback passed to "+functionName+" was not a string. ["+typeof callback+"]"
            );
        }
        if (!this._socket){
            throw new Error(
                "Socket not created before calling "+functionName+". Was _connect() called?"
            );
        }
    }

    /**
     * Check that arguments for request() or get/post/put/del() are valid.
     *
     * @param {string}   functionName The name of the function to be used in error messages.
     * @param {string}   url          The destination URL path; e.g. "/checkout".
     * @param {object}   [data]       Data to be included as the request body.
     * @param {function} [callback]   Takes a body object and an object with headers, body and statusCode.
     *
     * @protected
     * @throws ReferenceError When an argument is missing.
     * @throws TypeError      When an argument is the wrong type.
     * @throws Error          When the socket doesn't exist.
     */
    _validateRequest(functionName, url, data, callback){
        // Check URL passed
        if (!url){
            throw new ReferenceError(
                "No `url` passed to "+functionName+"."
            );
        }

        // Check URL is a string
        if (typeof url !== 'string'){
            throw new TypeError(
                "`url` passed to "+functionName+" was not a string. ["+typeof url+"]"
            );
        }

        // Check data is an object if it was passed
        if (data && typeof data !== 'object'){
            throw new TypeError(
                "`data` passed to "+functionName+" was not an object. ["+typeof data+"]"
            );
        }

        // Check callback is a function
        if (callback && typeof callback !== 'function'){
            throw new TypeError(
                "`callback` passed to "+functionName+" was not a function. ["+typeof callback+"]"
            );
        }

        // Check socket exists
        if (!this._socket){
            throw new Error(
                "Socket not created before calling "+functionName+". Was _connect() called?"
            );
        }
    }

    // </editor-fold> Validation
}

export default Sails;

/**
 * @class Description of RealTimeService
 * @memberOf module:realtime/api
 */
class AbstractRealTimeAPI {
    /**
     * @param {RealTimeService} RealTimeService
     */
    constructor(RealTimeService) {
        var className = this.constructor.name;
        if (className === 'AbstractRealTimeAPI'){
            throw new Error("AbstractRealTimeAPI is not supposed to be instantiated");
        }
        if (!RealTimeService){
            throw new ReferenceError("RealTimeService not passed to "+className);
        }

        /**
         * @member {RealTimeService}
         */
        this.service = RealTimeService;

        /**
         * A 2D map of channels and actions to a list of functions
         * @member {object.<string, object.<string, function[]>>}
         * @private
         */
        this._responseMap = {};
    }

    /**
     * Indicate which methods `RealTimeResponse`s should be passed to,
     * based on their `channel` and `action`.
     *
     * @param {string} channel
     * @param {string} action
     * @param {function} method
     * @protected
     */
    _mapResponse(channel, action, method){
        // Ensure action object exists for channel
        if (!this._responseMap[channel]){
            this._responseMap[channel] = {};
        }

        // Ensure method array exists for action
        if (!this._responseMap[channel][action]){
            this._responseMap[channel][action] = [];
        }

        // Add method to array if not already there
        if (this._responseMap[channel][action].indexOf(method) < 0){
            this._responseMap[channel][action].push(method);
        }
    }

    /**
     * Pass the response to the methods mapped with _mapResponse().
     *
     * @param {RealTimeResponse} response
     * @param {...*} args Any other arguments to pass to mapped methods.
     * @protected
     */
    _routeResponse(response, ...args) {
        var channel = response.channel;
        var action = response.action;

        // Check it can be routed
        if (!channel || !action){
            return;
        }

        //TODO Remove this log when it's production ready
        if (!this._responseMap[channel]){
            console.info(
                "Channel '"+channel+"' not mapped for "+this.constructor.name+"."
            );
        } else if (!this._responseMap[channel][action]){
            console.info(
                "Action '"+action+"' not mapped for "+this.constructor.name+" channel '"+channel+"'."
            );
        }

        // Check it can be routed
        if (!this._responseMap[channel] || !this._responseMap[channel][action]){
            return;
        }

        // Pass to methods
        args.unshift(response); // Add response to args
        var methods = this._responseMap[channel][action]; // Get methods
        for (var i in methods){
            methods[i].apply(this, args);
        }
    }

}

export default AbstractRealTimeAPI;

/**
 * @class The standard result for most Player responses
 * @memberOf module:realtime/api
 */
class RealTimeResponse {
    constructor(rawObject) {
        this._action  = rawObject.action;
        this._channel = rawObject.channel;
        this._key     = rawObject.key;

        this._data = {};
        for (var key in rawObject){
            if (key !== 'action' && key !== 'channel' && key !== 'key'){
                if (rawObject.hasOwnProperty(key)){
                    this._data[key] = rawObject[key];
                }
            }
        }
    }

    /**
     * The action to be taken with this data.
     * @example "comment.add"
     * @returns {string}
     */
    get action(){
        return this._action;
    }

    /**
     * The channel this response came from.
     * @example "feed"
     * @returns {string}
     */
    get channel(){
        return this._channel;
    }

    /**
     * The key of this response within its channel.
     * @example 12345
     * @returns {*}
     */
    get key(){
        return this._key;
    }

    /**
     * The payload of the response.
     * Anything that isn't a property of RealTimeResponse.
     * @returns {object}
     */
    get data(){
        return this._data;
    }
}

export default RealTimeResponse;

import AbstractRealTimeAPI from '../AbstractRealTimeAPI';
import RealTimeResponse    from '../RealTimeResponse';
import ActivityModel       from '../../../models/activity/ActivityModel';
import CommentModel        from '../../../models/activity/comment/CommentModel';

/**
 * @class Description of FeedRealTime
 * @memberOf module:realtime/api/feed
 */
class FeedRealTime extends AbstractRealTimeAPI {

    /** @inheritdoc */
    constructor(RealTimeService) {
        super(RealTimeService);

        // Add listeners
        this.service.on('feed-new', (data)=>{
            var args = [
                new RealTimeResponse(data),
                new ActivityModel(data.activity)
            ];
            this._routeResponse.apply(this, args);
        });
        this.service.on('feed', (data)=>{
            var args = [
                new RealTimeResponse(data),
                new ActivityModel(data.activity)
            ];
            if (data.comment) {
                args.push(new CommentModel(data.comment));
            }
            this._routeResponse.apply(this, args);
        });
    }

    // <editor-fold desc="Events">

    /**
     * Listen out for activities added to the feed
     * Can be triggered once you subscribeToFeed().
     * @param {function} callback
     */
    onActivityAdded(callback){
        this._mapResponse("feed-new", "activity.add", callback);
    }

    /**
     * Listen out for an activity being edited.
     * Can be triggered once you subscribeToActivity().
     * @param {function} callback
     */
    onActivityEdited(callback){
        this._mapResponse("feed", "activity.edit", callback);
    }
    /**
     * Listen out for an activity being deleted.
     * Can be triggered once you subscribeToActivity().
     * @param {function} callback
     */
    onActivityDeleted(callback){
        this._mapResponse("feed", "activity.delete", callback);
    }

    /**
     * Listen out for a comment being edited to an activity.
     * Can be triggered once you subscribeToActivity().
     * @param {function} callback
     */
    onCommentAdded(callback){
        this._mapResponse("feed", "comment.add", callback);
    }

    /**
     * Listen out for an activity's comment being edited.
     * Can be triggered once you subscribeToActivity().
     * @param {function} callback
     */
    onCommentEdited(callback){
        this._mapResponse("feed", "comment.edit", callback);
    }

    /**
     * Listen out for an activity's comment being deleted.
     * Can be triggered once you subscribeToActivity().
     * @param {function} callback
     */
    onCommentDeleted(callback){
        this._mapResponse("feed", "comment.delete", callback);
    }

    // </editor-fold> Events
    // <editor-fold desc="Subscribe">

    /**
     * Subscribe to tabs in the feed.
     * @param {string[]} tabs
     */
    subscribeToFeed(tabs){
        if (!tabs) tabs = ['following', 'discover'];
        var params = { channel:'feed-new', key:tabs };
        this.service.post('/rooms', params);
    }

    /**
     * Subscribe to a specific activity
     * @param {int} activityId
     */
    subscribeToActivity(activityId){
        var functionName = this.constructor.name+".subscribeToActivity()";

        // Check activityId
        if (!activityId) {
            throw new Error("No activityId passed to "+functionName+".");
        }
        if (typeof activityId !== 'number'){
            throw new TypeError(
                "activityId passed to "+functionName+" wasn't a number. ["+typeof activityId+"]"
            );
        }
        if (activityId <= 0){
            throw new RangeError(
                "activityId passed to "+functionName+" wasn't at least 1. ["+activityId+"]"
            );
        }

        // Execute
        var params = { channel:'feed', key:activityId };
        this.service.post('/rooms', params);
    }

    /**
     * Subscribe to specific activities
     * @param {int[]} activityIds
     */
    subscribeToActivities(activityIds){
        var functionName = this.constructor.name+".subscribeToActivities()";
        // Check activityIds
        if (!activityIds) {
            throw new Error("No activityIds passed to "+functionName+".");
        }
        if (!Array.isArray(activityIds)){
            throw new TypeError(
                "activityIds passed to "+functionName+ " wasn't an array. ["+typeof activityIds+"]"
            );
        }

        // Check each activityId
        for (let i in activityIds){
            let activityId = activityIds[i];
            if (typeof activityId !== 'number'){
                throw new TypeError(
                    "activityId passed to "+functionName+" wasn't a number. ["+typeof activityId+" at index "+i+"]"
                );
            }
            if (activityId <= 0){
                throw new RangeError(
                    "activityId passed to "+functionName+" wasn't at least 1. ["+activityId+" at index "+i+"]"
                );
            }
        }
        var params = { channel:'feed', key:activityIds };
        this.service.post('/rooms', params);
    }

    // </editor-fold> Subscribe
}

export default FeedRealTime;

import AbstractRealTimeAPI from '../AbstractRealTimeAPI';
import NotificationModel   from '../../../models/notification/NotificationModel';

/**
 * @class Description of FeedRealTime
 * @memberOf module:realtime/api/notifications
 */
class NotificationsRealTime extends AbstractRealTimeAPI {

    /** @inheritdoc */
    constructor(RealTimeService) {
        super(RealTimeService);
    }

    /**
     * TODO What is this for? No data to pass?
     * @param {function} callback
     * @example onClearUnreadFlag(function(){ ... });
     */
    onClearUnreadFlag(callback){
        this.service.on('notifications:clear_unread_flag', function(){
            console.info('notifications:clear_unread_flag', arguments);
            callback(); // No data to pass
        });
    }

    /**
     * Callback called when a notification has been read
     * @param {function} callback Accepts the notification type and key strings
     * @example onMarkRead(function(notificationType, notificationKey){ ... });
     */
    onMarkRead(callback){
        this.service.on('notifications:mark_read', function(data){
            /**
             * Example `data.set`: "like_comment:c5391614"
             * Example `data.set`: "mention_comment:c5391614"
             * Example `data.set`: "reply_comment:c5391614"
             * @type {string}
             */
            var set = data.set;
            var split = set.split(':', 2);
            var notificationType = split[0];
            var notificationKey = split[1];

            callback(notificationType, notificationKey);
        });
    }

    /**
     * Callback called when marking all notifications as read
     * @param {function} callback Takes nothing
     * @example onMarkReadAll(function(){ ... });
     */
    onMarkReadAll(callback){
        this.service.on('notifications:mark_all_read', function(){
            console.info('notifications:mark_all_read', arguments);
            callback();
        });
    }

    /**
     * Callback is applied when a new notification is created
     * @param {function} callback Takes an array of NotificationModels in the shortlist
     * @example onNew(function(notifications){ ... });
     */
    onNew(callback){
        this.service.on('notifications:new', function(data){
            try {
                var notifications = data.notifications.map((obj)=> {
                    return new NotificationModel(obj);
                });
                callback(notifications);
            }catch(e){
                console.error("NotificationsRealTime.onNew Error:", e);
            }
        });
    }
}

export default NotificationsRealTime;
